mapper.select:
SELECT * FROM ( SELECT plan.planName, plan.planId, route_conf.inspectRouteName, plan.cronType, plan.checkInType, plan.createTime, DATE_FORMAT(plan.createTime,'%Y-%m-%d %H:%i') createTimeStr, sys_user.true_name 'operatorName', plan.gapType, plan.status, ( SELECT GROUP_CONCAT(DISTINCT staff.staffName) 'staffNames' FROM project_inspect_plan_shift3 shift LEFT JOIN project_inspect_plan_shift_staff shift_staff ON shift_staff.recordId = shift.recordId LEFT JOIN project_staff staff ON staff.staffId = shift_staff.staffId WHERE shift.planId = plan.planId AND shift.projectId = plan.projectId ) 'staffNames', CONCAT(STR_TO_DATE((DATE_FORMAT(any_value(plan.startTime),'%Y-%m-%d')),'%Y-%m-%d'),' è‡³ ',STR_TO_DATE((DATE_FORMAT(any_value(plan.endTime),'%Y-%m-%d')),'%Y-%m-%d')) 'beginEndTime' FROM project_inspect_plan plan LEFT JOIN pigxx.sys_user sys_user ON sys_user.user_id = plan.operator LEFT JOIN project_inspect_route_conf route_conf ON plan.inspectRouteId = route_conf.inspectRouteId
plan.planName LIKE concat('%',#{query.keyword},'%')
) AS plan_cus_table
AND STR_TO_DATE((DATE_FORMAT(createTime,'%Y-%m-%d')),'%Y-%m-%d') BETWEEN #{query.timeRange[0]} AND #{query.timeRange[1]}
AND cronType = #{query.cronType}
AND status = #{query.status}
ORDER BY createTime desc
